// --------------
/* Function: iqrf.embed.coordinator.ReadRemotelyBondedMid_Request
Encodes DPA request to read remotely bonded nodes.

Returns:
  string: DPA request string.
*/
iqrf.embed.coordinator.ReadRemotelyBondedMid_Request = function ()
{
  return new iqrf.DpaRawHdpMessageCoordinator( iqrf.PNUM_Coordinator, '0F' );
};

/* Function: iqrf.embed.coordinator.ReadRemotelyBondedMid_Response
Decodes DPA response from reading remotely bonded nodes.

Parameters:
  response - string: DPA response string.

Returns:
  array: Array of objects for every prebonded node. The object has the following fields:

* mid - number: Prebonded mid.
* userData - array: Array of 4 bytes with user data.
*/
iqrf.embed.coordinator.ReadRemotelyBondedMid_Response = function ( response )
{
  var responseData = iqrf.CheckResponsePnumPcmdDlen( response, iqrf.PNUM_Coordinator, '8F' );

  var result = [];
  var responseData_length = responseData.length;
  for ( var index = 0; index < responseData_length; index += 8 )
  {
    result[result.length] =
      {
        mid: responseData[index] + ( responseData[index + 1] * 0x100 ) + ( responseData[index + 2] * 0x10000 ) + ( responseData[index + 3] * 0x1000000 ),
        userData: [responseData[index + 4], responseData[index + 5], responseData[index + 6], responseData[index + 7]]
      };
  }

  return result;
};

